import React from "react";
import { Link } from "react-router-dom";
import "./card.css";
import PropTypes from "prop-types";
import { Card, Icon, Image } from 'semantic-ui-react'

const card = (props) => {
  const numberWithCommas = (x) => {
    return x.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
  };

  return (
    <div className="card_product mt-3 mb-3">
      <Card>
    <Image src='/images/avatar/large/matthew.png' wrapped ui={false} />
    <Card.Content>
      <Card.Header>{props.title}</Card.Header>
      <Card.Meta>
        <span className='date'>Joined in 2015</span>
      </Card.Meta>
      <Card.Description>
      {props.description}
      </Card.Description>
    </Card.Content>
    
  </Card>
      <h3 className="card__title">{props.title}</h3>
      <div className="card__header">
        <img
          className="card__header__photo"
          src={props.photo_main}
          alt={props.title}
        />
      </div>
      <p className="card__location">{props.description}</p>
      <div className="row">
        <div className="col-2-of-3">
          <p className="card__info">Price: ${numberWithCommas(props.price)}</p>
          {/* <p className='card__info'>Bedrooms: {props.bedrooms}</p>
                    <p className='card__info'>Bathrooms: {props.bathrooms}</p> */}
        </div>
        {/* <div className='col-1-of-3'>
                    <p className='card__saletype'>{props.sale_type}</p>
                    <p className='card__hometype'>{props.home_type}</p>
                    <p className='card__sqft'>Sqft: {props.sqft}</p>
                </div> */}
      </div>
      <Link className="card__link" to={`/products/${props.id}`}>
        View description
      </Link>
    </div>
  );
};

card.propTypes = {
  title: PropTypes.string.isRequired,
  description: PropTypes.string.isRequired,
  price: PropTypes.number.isRequired,
  photo_main: PropTypes.string.isRequired,
};

export default card;
