import React from "react";
import { loadStripe } from "@stripe/stripe-js";
import {
  Elements,
  CardElement,
  useStripe,
  useElements,
} from "@stripe/react-stripe-js";
import { Button } from "react-bootstrap";
import axios from "axios";
import Order from "./order";
import { connect } from "react-redux";

const CheckoutForm = ({ success }, { user, cart }) => {
  const stripe = useStripe();
  const elements = useElements();

  const handleSubmit = async (event) => {
    event.preventDefault();
    const { token } = await stripe.createToken ()


    const { error, paymentMethod } = await stripe.createPaymentMethod({
      type: "card",
      card: elements.getElement(CardElement),
    });

    if (!error) {
      console.log(stripe);
      const { id } = paymentMethod;
      const user_id = 1;
      try {
        const config = {
          headers: {
            "Content-Type": "application/json",
            Authorization: `JWT ${localStorage.getItem("access")}`,
            Accept: "application/json",
          },
        };
        const { data } = await axios.post(
          `http://127.0.0.1:8000/products/checkout/`,
          { id, user_id },
          config
        );
        console.log(data);
        success();
      } catch (error) {
        console.log(error);
      }
    }
  };

  return (
    <form
      className="mt-3 mr-5"
      onSubmit={handleSubmit}
      style={{ width: "50%" }}
    >
      <h3>Would you complete your purchase ?</h3>
      <CardElement />
      <Button type="submit" disabled={!stripe} style={{ width: "100%" }}>
        Pay
      </Button>
    </form>
  );
};
const mapSTP = (state) => ({
  user: state.account.user,
  cart: state.product.cart,
});
connect(mapSTP)(CheckoutForm);

// you should use env variables here to not commit this
// but it is a public key anyway, so not as sensitive
const stripePromise = loadStripe(
  "pk_test_51HpWUdAHdk3LQCR4NdO1v4XGdIci4KVffOBjlY3p84Y65CDxa2wz3NgaGAxoLDAFDbh8UoAy85cqHWNd5I5MyypH00fcFtkQqs"
);
const InjectedForm = injectStripe(CheckoutForm);
const Index = () => {
  const [status, setStatus] = React.useState("ready");

  if (status === "success") {
    return <div>Congrats on your payment!</div>;
  }

  return (
    <Elements stripe={stripePromise}>
      <div style={{ display: "flex" }}>
        <CheckoutForm
          success={() => {
            setStatus("success");
          }}
        />
        <Order />
      </div>
    </Elements>
  );
};

export default Index;
