import React, { Component } from "react";
import { fetchCart } from "../../JS/actions/product";
import { connect } from "react-redux";

class OrderSummary extends Component {
  componentDidMount() {
    const fetchData = async () => {
      try {
        await this.props.fetchCart(this.props.user.id);
      } catch (err) {}
    };

    fetchData();
    console.log(this.props.cart);
  }
  render() {
    return (
      <div>
        <div className="card mt-1" style={{ width: "100%" }}>
          <div className="card-body">
            <div className="table-responsive">
              <table className="table product-table ">
                <thead className="mdb-color lighten-5">
                  <tr>
                    <th></th>
                    <th className="font-weight-bold">
                      <strong>Product</strong>
                    </th>

                    <th></th>
                    <th className="font-weight-bold">
                      <strong>Price</strong>
                    </th>
                    <th className="font-weight-bold">
                      <strong>uantity</strong>
                    </th>
                    <th className="font-weight-bold">
                      <strong>Total Item</strong>
                    </th>
                    <th></th>
                  </tr>
                </thead>

                <tbody>
                 {}
                  <tr>
                    <td colSpan="3"></td>
                    <td>
                      <h4 className="mt-2">
                        <strong>Total</strong>
                      </h4>
                    </td>
                    <td className="text-right">
                      <h4 className="mt-2">
                        <strong>$2600</strong>
                      </h4>
                    </td>
                    <td colSpan="3" className="text-right">
                      <button type="button" className="btn btn-primary btn-rounded">
                        Complete purchase
                        <i className="fas fa-angle-right right"></i>
                      </button>
                    </td>
                  </tr>
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => ({
  user: state.account.user,
  cart: state.product.cart,
});
export default connect(mapStateToProps, { fetchCart })(OrderSummary);
