import React from "react";
import { Link } from "react-router-dom";
import "./card.css";
import PropTypes from "prop-types";
import {
  Container,
  Dimmer,
  Image,
  Item,
  Label,
  Loader,
  Message,
  Segment
} from "semantic-ui-react";
const card = (props) => {
  const numberWithCommas = (x) => {
    return x.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
  };

  return (
    <Item key={props.id}>
                <Item.Image src={props.image} />
                <Item.Content>
                  <Item.Header>
                    {props.title}
                  </Item.Header>
                  <Item.Meta>
                    <span className="cinema">{props.category}</span>
                  </Item.Meta>
                  <Item.Description>{props.description}</Item.Description>
                  <Item.Extra>
                    {/* <Button
                      primary
                      floated="right"
                      icon
                      labelPosition="right"
                      onClick={() => this.handleAddToCart(item.slug)}
                    >
                      Add to cart
                      <Icon name="cart plus" />
                    </Button> */}
                    {props.discount_price && (
                      <Label
                        color={
                          props.label === "primary"
                            ? "blue"
                            : props.label === "secondary"
                            ? "green"
                            : "olive"
                        }
                      >
                        {props.label}
                      </Label>
                    )}
                  </Item.Extra>
                </Item.Content>
              </Item>
  );
};

card.propTypes = {
  title: PropTypes.string.isRequired,
  description: PropTypes.string.isRequired,
  price: PropTypes.number.isRequired,
  photo_main: PropTypes.string.isRequired,
};

export default card;
