const CheckoutForm = ({ success, user }) => {
    const stripe = useStripe();
    const elements = useElements();
  
    const handleSubmit = async (event) => {
      event.preventDefault();
  
      const { error, paymentMethod } = await stripe.createPaymentMethod({
        type: "card",
        card: elements.getElement(CardElement),
      });
  
      if (!error) {
        const user_id = user.id;
        try {
          const config = {
            headers: {
              "Content-Type": "application/json",
              Authorization: `JWT ${localStorage.getItem("access")}`,
              Accept: "application/json",
            },
          };
          const { data } = await axios.post(
            `http://127.0.0.1:8000/products/checkout/`,
            { payment_method_id: paymentMethod.id, user_id },
            config
          );
          console.log(data);
          success();
        } catch (error) {
          console.log(error);
        }
      }
    };
  
    return (
      <form
        className="mt-3 mr-5"
        onSubmit={handleSubmit}
        style={{ width: "50%" }}
      >
        <h3>Would you complete your purchase ?</h3>
        <CardElement />
        <Button type="submit" disabled={!stripe} style={{ width: "100%" }}>
          Pay
        </Button>
      </form>
    );
  };
  const mapSTP = (state) => ({
    user: state.account.user,
    cart: state.product.cart,
  });
  connect(mapSTP)(CheckoutForm);
