import React, { useEffect } from "react";
import { loadStripe } from "@stripe/stripe-js";
import {
  Elements,
  CardElement,
  useStripe,
  useElements,
} from "@stripe/react-stripe-js";
import { Button,Form } from "react-bootstrap";
import axios from "axios";
import Order from "./order";

const CheckoutForm = ({ success }) => {
  const stripe = useStripe();
  const elements = useElements();

  const handleSubmit = async (event) => {
    event.preventDefault();

    const { error, paymentMethod } = await stripe.createPaymentMethod({
      type: "card",
      card: elements.getElement(CardElement),
    });

    if (!error) {
      console.log(stripe);
      const { id } = paymentMethod;

      // try {
      //   const { data } = await axios.post("/api/charge", { id, amount: 1099 });
      //   console.log(data);
      //   success();
      // } catch (error) {
      //   console.log(error);
      // }
    }
  };

  return (
    <form
      className="mt-3 mr-5"
      onSubmit={handleSubmit}
      style={{ width: "50%" }}
    >
      <Form onSubmit={this.handleSubmit} success={success} error={error}>
        <Form.Input
          required
          name="street_address"
          placeholder="Street address"
          // onChange={this.handleChange}
          // value={formData.street_address}
        />
        <Form.Input
          required
          name="apartment_address"
          placeholder="Apartment address"
          // onChange={this.handleChange}
          // value={formData.apartment_address}
        />
        <Form.Field required>
          <Select
            loading={countries.length < 1}
            fluid
            clearable
            search
            options={countries}
            name="country"
            placeholder="Country"
            // onChange={this.handleSelectChange}
            // value={formData.country}
          />
        </Form.Field>
        <Form.Input
          required
          name="zip"
          placeholder="Zip code"
          onChange={this.handleChange}
          value={formData.zip}
        />
        <Form.Checkbox
          name="default"
          label="Make this the default address?"
          onChange={this.handleToggleDefault}
          checked={formData.default}
        />
      </Form>
      <h3>Would you complete your purchase ?</h3>
      <CardElement />
      <Button type="submit" disabled={!stripe} style={{ width: "100%" }}>
        Pay
      </Button>
    </form>
  );
};

