// import React from "react";
import { Link } from "react-router-dom";
import "./card.css";
import PropTypes from "prop-types";
import { MDBBtn, MDBIcon } from 'mdbreact';


function Card(props) {
  const numberWithCommas = (x) => {
    return x.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
  };
  return (
      <div className="card_product mt-3 mb-3">
      <h3 className="card__title">{props.title}</h3>
      <div className="card__header">
        <img
          className="card__header__photo"
          src={props.photo_main}
          alt={props.title}
        />
      </div>
      <p className="card__location">{props.description}</p>
      <div className="row">
        <div className="col-2-of-3">
          <p className="card__info">Price: ${numberWithCommas(props.price)}</p>
      </div>
      <Link className="card__link" to={`/products/${props.id}`}>
        View description
      </Link>
      <MDBBtn rounded floating color='fb'>
                <MDBIcon size='lg' fab icon='facebook-f'></MDBIcon>
              </MDBBtn>

    </div>
    </div>
  )
}

Card.propTypes = {
  title : PropTypes.string.isRequired,
  description: PropTypes.string.isRequired,
  price: PropTypes.number.isRequired,
  photo_main: PropTypes.string.isRequired,
};

export default Card;
