import React, { useState } from "react";
import { connect } from "react-redux";
import "./profile.css";
import { Button, Alert, Modal } from "react-bootstrap";
import { deleteUser } from "../../JS/actions/account";
import { Redirect } from "react-router-dom";
// import ModalConfirm from "../modalConfirm/modalConfirm";
// import { message } from "antd";
import axios from 'axios'
function ModalConfirm(props) {
  const [show, setShow] = useState(false);
  const [current_password, setPassword] = useState("");

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const handelchange = (e) => {
    setPassword(e.target.value);
  };
  const handleConfirm = () => {
    const config = {
      headers: {
        "Content-Type": "application/json",
        Authorization: `JWT ${localStorage.getItem("access")}`,
        Accept: "application/json",
      },
    };  
    axios.delete(`http://127.0.0.1:8000/auth/users/me/`, current_password,config);
    handleClose();
  };

  return (
    <>
      <Button variant="danger" onClick={handleShow}>
        Delete account
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Delete Account</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <label> Current Password</label>
          <input
            type="password"
            name="password"
            className="form-control"
            placeholder="Current Password"
            onChange={handelchange}
            value={current_password}
          />
        </Modal.Body>

        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Cancel
          </Button>
          <Button variant="primary" onClick={handleConfirm}>
            confirm
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

function ProfileUser(props) {
  const [updatePassword, setPassword] = useState({
    password: "",
    repeatPassword: "",
  });

  // const [updatedUser, setUpdatedUser] = useState({
  //   name: props.user ? props.user.name : "",
  //   email: props.user ? props.user.email : "",
  // });
  // const [id] = useState(props.user ? props.user._id : "");

  // const handleChange = (e) => {
  //   setUpdatedUser({ ...updatedUser, [e.target.name]: e.target.value });
  //   setPassword({ ...updatePassword, [e.target.name]: e.target.value });
  // };
  // const updateUser = ({ updatedUser, id }) => {
  //   props.updateUser({ updatedUser, id });
  //   if (!props.error.id) {
  //     message.success("Account updated !!!");
  //   }
  // };

  return !props.user ? (
    <Redirect to="/login"></Redirect>
  ) : (
    <div className="container-profile bg-light">
      <h3>My account</h3>
      <div className="container-edit">
        <label>Your Name</label>
        <input
          type="text"
          className="form-control"
          placeholder="Entre Name"
          name="name"
        />
        <label>Your adress email</label>

        <input
          type="text"
          placeholder="Email"
          name="email"
          className="form-control"
        ></input>
        <Button className="btn-update">Update</Button>
      </div>
      <h5>Change your password</h5>
      <div className="container-edit">
        <label> Current Password</label>
        <input
          type="password"
          name="password"
          className="form-control"
          placeholder="Current Password"
        />
        <label>New Password</label>
        <input
          type="password"
          name="repeatPassword"
          className="form-control"
          placeholder="New Password"
        />
        <Button className=" btn-update">Confirm</Button>
      </div>
      <div className="remove-account mb-4">
        <h6>Remove Account</h6>
        <p>You have the option to delete your account</p>
        <p>NB: you will can't login with this account again </p>
        <ModalConfirm deleteUser={props.deleteUser} />
      </div>
    </div>
  );
}
const mapStateToProps = (state) => {
  return {
    user: state.account.user,
    token: state.account.access,
    error: state.error,
  };
};
export default connect(mapStateToProps, { deleteUser })(ProfileUser);
