import { START_CART, CART_SUCCESS, CART_FAIL, DELETE_SUCCESS } from "../constants";
const initialeState = {
  loading: false,
  cart: JSON.parse(localStorage.getItem("cart")),
};

const productReducer = (state = initialeState, { type, payload }) => {
  switch (type) {
    case START_CART:
      return { ...state, loading: true };
    case CART_SUCCESS:
      localStorage.setItem("cart", JSON.stringify(payload));
      return { ...state, loading: false, cart: payload };
    case DELETE_SUCCESS:
      return {...state,loading:false, cart:state.cart.filter(item=>id!=payload)}
    case CART_FAIL:
      return { ...state, loading: false };
    default:
      return state;
  }
};

export default productReducer;
