import React from "react";
import { loadStripe } from "@stripe/stripe-js";
import { Elements } from "@stripe/react-stripe-js";

import Order from "./order";

import CheckoutForm from "./checkoutForm";
// you should use env variables here to not commit this
// but it is a public key anyway, so not as sensitive
const stripePromise = loadStripe(
  "pk_test_51HpWUdAHdk3LQCR4NdO1v4XGdIci4KVffOBjlY3p84Y65CDxa2wz3NgaGAxoLDAFDbh8UoAy85cqHWNd5I5MyypH00fcFtkQqs"
);

const Index = () => {
  const [status, setStatus] = React.useState("ready");

  if (status === "success") {
    return (
      <div className="d-flex justify-content-center">
        <i
          class="fa fa-check-circle fa-5x"
          style={{ color: "green" }}
          aria-hidden="true"
        ></i>
        <h1>Congrats on your payment!</h1>
      </div>
    );
  }

  return (
    <div className="d-flex justify-content-center">
      <div>
        <i
          class="fa fa-check-circle fa-5x p-2"
          style={{ color: "green" }}
          aria-hidden="true"
        ></i>
        <h1>Congrats on your payment!</h1>
      </div>
    </div>
  );
};

export default Index;
