import 'bootstrap/dist/css/bootstrap.min.css';
import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

import Header from './components/layout/Header';
import Home from './components/layout/Home';
import ProductsDetails from './components/'
import Login from './components/accounts/Login';
import Signup from './components/accounts/Signup';
import Activate from './components/accounts/Activate';
import ResetPassword from './components/accounts/ResetPassword';
import ResetPasswordConfirm from './components/accounts/ResetPasswordConfirm';



class App extends Component {

  render() {
    return (
          <Router>
              <Header/>
              <div className="container">
                <Switch>
                    <Route exact path='/' component={Home} />
                    <Route exact path = '/:id' component = {ProductsDetails}/>
                    <Route exact path='/login' component={Login} />
                    <Route exact path='/register' component={Signup} />
                    <Route exact path='/reset_password' component={ResetPassword} />
                    <Route exact path='/password/reset/confirm/:uid/:token' component={ResetPasswordConfirm} />
                    <Route exact path='/activate/:uid/:token' component={Activate} />
                </Switch>
              </div>
          </Router>

    );
  }
}

export default App
