import React from "react";
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";
import { Button,Form } from "react-bootstrap";
import axios from "axios";
import { connect } from "react-redux";

const CheckoutForm = ({ success, user }) => {
  const stripe = useStripe();
  const elements = useElements();

  const handleSubmit = async (event) => {
    event.preventDefault();

    const { error, paymentMethod } = await stripe.createPaymentMethod({
      type: "card",
      card: elements.getElement(CardElement),
    });

    if (!error) {
      const user_id = user.id;
      try {
        const config = {
          headers: {
            "Content-Type": "application/json",
            Authorization: `JWT ${localStorage.getItem("access")}`,
            Accept: "application/json",
          },
        };
        const { data } = await axios.post(
          `http://127.0.0.1:8000/products/checkout/`,
          { payment_method_id: paymentMethod.id, user_id },
          config
        );
        console.log(data);
        success();
      } catch (error) {
        console.log(error);
      }
    }
  };

  return (
    <div className='w-50 m-5' >
      <Form>
  <Form.Group controlId="formBasicEmail">
    <Form.Label>Email address</Form.Label>
    <Form.Control type="email" placeholder="Enter email" />
    <Form.Text className="text-muted">
      We'll never share your email with anyone else.
    </Form.Text>
  </Form.Group>

  <Form.Group controlId="formBasicPassword">
    <Form.Label>Password</Form.Label>
    <Form.Control type="password" placeholder="Password" />
  </Form.Group>
  </Form>
    <form
      className="mt-3 mr-5"
      onSubmit={handleSubmit}
      style = {{width : '100%'}}
    >
      <h4>Would you complete your purchase ?</h4>
      <CardElement />
      <Button type="submit" disabled={!stripe} style={{ width: "100%" }}>
        Pay
      </Button>
    </form>
    </div>
  );
};
const mapSTP = (state) => ({
  user: state.account.user,
  cart: state.product.cart,
});
export default connect(mapSTP)(CheckoutForm);
