import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { Grid, Image } from "semantic-ui-react";

function ProductsDetails(props) {
  const [listing, setListing] = useState({});
  const [price, setPrice] = useState(0);

  const numberWithCommas = (x) => {
    return x.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
  };

  useEffect(() => {
    const id = props.match.params.id;
    const config = {
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
    };

    axios
      .get(`http://127.0.0.1:8000/products/${id}`, config)
      .then((res) => {
        setListing(res.data);
        setPrice(numberWithCommas(res.data.price));
      })
      .catch((err) => {});
  }, [props.match.params.id]);
  return (
    <div>
      <Grid verticalAlign="middle" columns={4} centered className="mt-3">
        <Grid.Row>
          <Grid.Column>
            <Image src={listing.photo_main} />
          </Grid.Column>
          <Grid.Column>
            {listing.photo_1 ? <Image src={listing.photo_1} alt="" /> : null}
            {listing.photo_2 ? <Image src={listing.photo_2} alt="" /> : null}
            {listing.photo_3 ? <Image src={listing.photo_3} alt="" /> : null}
            {listing.photo_4 ? <Image src={listing.photo_4} alt="" /> : null}
          </Grid.Column>
          <Grid.Column>
            <li className="listingdetail__list__item">Price: ${price}</li>
            <li className="listingdetail__list__item">
              Description: {listing.description}
            </li>
            
          </Grid.Column>
        </Grid.Row>
      </Grid>
    </div>
  );
}

export default ProductsDetails;
